mapper.select:
SELECT p.id, p.user_sp_id, p.user_sp_name, p.company,
p.message_content, p.phone_count, p.charge_count,
case when p.status
BETWEEN 100 and 199 then '待审核'
when p.status BETWEEN 200 and 299 then
'驳回'
when p.status BETWEEN 300 and 399 then '通过' else '' end status ,
case p.submit_type when '1' then 'HTTP' when '2' then 'CMPP' when '3'
then
'SMPP' else '' end submitType ,
p.submit_time, p.send_time,
p.error_count , p.status_description
from inter_sms_package p
where 1 =1 

	and p.company_id = ${formData.userid}


	and p.user_sp_id in ${formData.subAccountAppIds}


	and p.id >= #{formData.queryStartTime}


	and p.id < #{formData.queryEndTime}


	and p.id = #{formData.packageid}


	and p.status BETWEEN 100 and 199


	and p.status BETWEEN 200 and 299


	and p.status BETWEEN 300 and 399


	and p.user_sp_id=#{formData.spId}


	and p.user_sp_name = #{formData.spName} 

order by p.submit_time desc limit ${length} offset ${start}

SELECT count(p.id) count from inter_sms_package p
where 1 = 1

	and p.company_id = ${formData.userid}


	and p.user_sp_id in ${formData.subAccountAppIds}


	and p.id >= #{formData.queryStartTime}


	and p.id < #{formData.queryEndTime}


	and p.id = #{formData.packageid}


	and p.status BETWEEN 100 and 199


	and p.status BETWEEN 200 and 299


	and p.status BETWEEN 300 and 399


	and p.user_sp_id=#{formData.spId}


	and p.user_sp_name = #{formData.spName}

select s.id, s.mobile, s.send_channel_id, case s.status when 200
then
'提交成功' else '提交失败' end status , s.status as result,
s.dr_time, case
s.dr_result when 1 then '成功' when 0 then '' when -1 then '失败'
else ''
end dr_result,
s.dr_reason , s.send_time , s.package_id ,
p.user_sp_name, p.submit_time , p.message_content
from inter_sms_mt s ,
inter_sms_package p where s.package_id = p.id 

	and p.company_id = ${formData.userid}


	and p.user_sp_id in ${formData.subAccountAppIds}


	and s.id
	>= #{formData.queryStartTime}


	and s.id
	<#{formData.queryEndTime}


	and s.mobile like #{formData.mobile}||'%'


	and p.message_content like '%'||#{formData.content}||'%'


	and s.dr_result = ${formData.drresult}


	and p.user_sp_id=#{formData.spId}

order by s.send_time desc limit ${length} offset ${start}

select count(s.id) count
from inter_sms_mt s , inter_sms_package p
where s.package_id = p.id 

	and p.company_id = ${formData.userid}


	and p.user_sp_id in ${formData.subAccountAppIds}


	and s.id
	>= #{formData.queryStartTime}


	and s.id
	<#{formData.queryEndTime}


	and s.mobile like #{formData.mobile}||'%'


	and p.message_content like '%'||#{formData.content}||'%'


	and s.dr_result = ${formData.drresult}


	and p.user_sp_id=#{formData.spId}
